import { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "../UI/ExpensesFilter";
import "../UI/ExpensesFilter.css";
function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2020");
  const filterChangeHandler = (selectedYear) => {
    console.log("expenses.js");
    setFilteredYear(selectedYear);
    console.log(selectedYear);
    if (filteredYear === 2019) {
      setFilterInfoText("2020, 2021 and 22 is hidden");
    } else if (filteredYear === 2021) {
      setFilterInfoText("2019, 2020 and 22 is hidden");
    } else if (filteredYear === 2022) {
      setFilterInfoText("2019, 2020 and 21 is hidden");
    }
  };
  const [filterInfoText, setFilterInfoText] = useState(
    "2019, 2021 and 22 is hidden"
  );
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        <p>data for {filterInfoText} is hidden.</p>
        <ExpenseItem
          title={props.item[0].title}
          amount={props.item[0].amount}
          date={props.item[0].date}
        />
        <ExpenseItem
          title={props.item[1].title}
          amount={props.item[1].amount}
          date={props.item[1].date}
        />
        <ExpenseItem
          title={props.item[2].title}
          amount={props.item[2].amount}
          date={props.item[2].date}
        />
        <ExpenseItem
          title={props.item[3].title}
          amount={props.item[3].amount}
          date={props.item[3].date}
        />
      </Card>
    </div>
  );
}
export default Expenses;
